import React, { useState, useEffect, useMemo } from "react";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";
import "../styles/locations.css";

const EventModal = ({ isOpen, onClose, onSubmit, event = null, locations = [] }) => {
  const [formData, setFormData] = useState({
    name: "",
    description: "",
    presenter: "",
    time: "",
    locId: "",
  });

  useEffect(() => {
    if (event) {
      setFormData({
        name: event.name || "",
        description: event.description || "",
        presenter: event.presenter || "",
        time: event.time || "",
        locId: event.locId || "",
      });
    } else {
      setFormData({
        name: "",
        description: "",
        presenter: "",
        time: "",
        locId: "",
      });
    }
  }, [event]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(formData);
    onClose();
  };

  if (!isOpen) return null;

  return (
    <div
      style={{
        position: "fixed",
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        backgroundColor: "rgba(0,0,0,0.5)",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        zIndex: 1000,
      }}
    >
      <div
        style={{
          backgroundColor: "white",
          padding: "2rem",
          borderRadius: "8px",
          width: "90%",
          maxWidth: "500px",
          overflowY: "auto",
        }}
      >
        <h3>{event ? "Edit Event" : "Add New Event"}</h3>

        <form onSubmit={handleSubmit}>
          <label>Event Title *</label>
          <input type="text" name="name" value={formData.name} onChange={handleChange} required />

          <label>Description</label>
          <textarea name="description" value={formData.description} onChange={handleChange} rows="3" />

          <label>Presenter</label>
          <input type="text" name="presenter" value={formData.presenter} onChange={handleChange} />

          <label>Time *</label>
          <input
            type="text"
            name="time"
            placeholder="YYYY-MM-DD HH:mm"
            required
            value={formData.time}
            onChange={handleChange}
          />

          <label>Venue *</label>
          <select name="locId" value={formData.locId} onChange={handleChange} required>
            <option value="">Select a venue</option>
            {locations.map((loc) => (
              <option key={loc.id} value={loc.id}>
                {loc.name}
              </option>
            ))}
          </select>

          <div style={{ marginTop: "1rem", display: "flex", gap: "1rem" }}>
            <button type="button" onClick={onClose}>
              Cancel
            </button>
            <button type="submit">{event ? "Update" : "Add"} Event</button>
          </div>
        </form>
      </div>
    </div>
  );
};

// 描述文本组件，支持折叠/展开
const DescriptionText = ({ text, maxLength = 100 }) => {
  const [isExpanded, setIsExpanded] = useState(false);
  
  if (!text) return <span style={{ color: "#999" }}>No description</span>;
  
  const shouldTruncate = text.length > maxLength && !isExpanded;
  const displayText = shouldTruncate ? text.substring(0, maxLength) + "..." : text;
  
  return (
    <div>
      <span>{displayText}</span>
      {text.length > maxLength && (
        <button
          onClick={() => setIsExpanded(!isExpanded)}
          style={{
            marginLeft: "0.5rem",
            color: "#2c7be5",
            background: "none",
            border: "none",
            cursor: "pointer",
            fontSize: "0.85rem",
            textDecoration: "underline"
          }}
        >
          {isExpanded ? "less" : "more"}
        </button>
      )}
    </div>
  );
};

// 分页组件
const Pagination = ({ currentPage, totalPages, onPageChange }) => {
  const maxVisiblePages = 5;
  
  const getPageNumbers = () => {
    const pages = [];
    
    if (totalPages <= maxVisiblePages) {
      for (let i = 1; i <= totalPages; i++) {
        pages.push(i);
      }
    } else {
      let startPage = Math.max(1, currentPage - Math.floor(maxVisiblePages / 2));
      let endPage = startPage + maxVisiblePages - 1;
      
      if (endPage > totalPages) {
        endPage = totalPages;
        startPage = Math.max(1, endPage - maxVisiblePages + 1);
      }
      
      for (let i = startPage; i <= endPage; i++) {
        pages.push(i);
      }
    }
    
    return pages;
  };

  const pageNumbers = getPageNumbers();

  if (totalPages <= 1) return null;

  return (
    <div style={{
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      gap: "0.5rem",
      marginTop: "2rem",
      padding: "1rem",
      flexWrap: "wrap"
    }}>
      <button
        onClick={() => onPageChange(1)}
        disabled={currentPage === 1}
        style={{
          padding: "0.5rem 1rem",
          backgroundColor: currentPage === 1 ? "#e9ecef" : "#fff",
          color: currentPage === 1 ? "#6c757d" : "#2c7be5",
          border: "1px solid #dee2e6",
          borderRadius: "4px",
          cursor: currentPage === 1 ? "not-allowed" : "pointer",
          fontSize: "0.9rem"
        }}
      >
        First
      </button>
      
      <button
        onClick={() => onPageChange(currentPage - 1)}
        disabled={currentPage === 1}
        style={{
          padding: "0.5rem 1rem",
          backgroundColor: currentPage === 1 ? "#e9ecef" : "#fff",
          color: currentPage === 1 ? "#6c757d" : "#2c7be5",
          border: "1px solid #dee2e6",
          borderRadius: "4px",
          cursor: currentPage === 1 ? "not-allowed" : "pointer",
          fontSize: "0.9rem"
        }}
      >
        Previous
      </button>
      
      {pageNumbers[0] > 1 && (
        <span style={{ color: "#6c757d", padding: "0 0.5rem" }}>...</span>
      )}
      
      {pageNumbers.map(page => (
        <button
          key={page}
          onClick={() => onPageChange(page)}
          style={{
            padding: "0.5rem 1rem",
            backgroundColor: currentPage === page ? "#2c7be5" : "#fff",
            color: currentPage === page ? "#fff" : "#2c7be5",
            border: "1px solid #dee2e6",
            borderRadius: "4px",
            cursor: "pointer",
            fontWeight: currentPage === page ? "bold" : "normal",
            fontSize: "0.9rem"
          }}
        >
          {page}
        </button>
      ))}
      
      {pageNumbers[pageNumbers.length - 1] < totalPages && (
        <span style={{ color: "#6c757d", padding: "0 0.5rem" }}>...</span>
      )}
      
      <button
        onClick={() => onPageChange(currentPage + 1)}
        disabled={currentPage === totalPages}
        style={{
          padding: "0.5rem 1rem",
          backgroundColor: currentPage === totalPages ? "#e9ecef" : "#fff",
          color: currentPage === totalPages ? "#6c757d" : "#2c7be5",
          border: "1px solid #dee2e6",
          borderRadius: "4px",
          cursor: currentPage === totalPages ? "not-allowed" : "pointer",
          fontSize: "0.9rem"
        }}
      >
        Next
      </button>
      
      <button
        onClick={() => onPageChange(totalPages)}
        disabled={currentPage === totalPages}
        style={{
          padding: "0.5rem 1rem",
          backgroundColor: currentPage === totalPages ? "#e9ecef" : "#fff",
          color: currentPage === totalPages ? "#6c757d" : "#2c7be5",
          border: "1px solid #dee2e6",
          borderRadius: "4px",
          cursor: currentPage === totalPages ? "not-allowed" : "pointer",
          fontSize: "0.9rem"
        }}
      >
        Last
      </button>
      
      <span style={{ marginLeft: "1rem", color: "#6c757d", fontSize: "0.9rem" }}>
        Page {currentPage} of {totalPages}
      </span>
    </div>
  );
};

// 页面大小选择器
const PageSizeSelector = ({ pageSize, onChange }) => {
  const options = [10, 25, 50, 100];
  
  return (
    <div style={{
      display: "flex",
      alignItems: "center",
      gap: "0.5rem",
      marginTop: "1rem"
    }}>
      <label style={{ fontSize: "0.9rem", color: "#666" }}>
        Show:
      </label>
      <select
        value={pageSize}
        onChange={(e) => onChange(Number(e.target.value))}
        style={{
          padding: "0.4rem 0.6rem",
          border: "1px solid #ccc",
          borderRadius: "4px",
          backgroundColor: "#fff",
          fontSize: "0.9rem"
        }}
      >
        {options.map(option => (
          <option key={option} value={option}>
            {option} per page
          </option>
        ))}
      </select>
    </div>
  );
};

 

export default AdminEventsPage;
