import React, { useState, useEffect, useMemo } from "react";
import "../styles/locations.css";

const FavouritesPage = () => {
  const [favoriteLocations, setFavoriteLocations] = useState([]);
  const [allLocations, setAllLocations] = useState([]);
  const [search, setSearch] = useState("");
  const [loading, setLoading] = useState(true);
  const [user, setUser] = useState(null);

  // 获取当前用户信息（从本地存储或API）
  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    }
  }, []);

  // 获取所有地点数据
  useEffect(() => {
    const fetchAllLocations = async () => {
      try {
        const res = await fetch("http://localhost:3000/locations", {
          headers: {
            "Content-Type": "application/json",
          },
        });

        if (!res.ok) {
          throw new Error(`Server error: ${res.status}`);
        }

        const data = await res.json();
        
        // 添加模拟的事件数量（实际应用中应该从后端获取）
        const enriched = data.map((loc, index) => ({
          id: loc.id || index + 1,
          name: loc.name,
          events: Math.floor(Math.random() * 10) + 1,
          area: loc.area || "Unknown",
        }));

        setAllLocations(enriched);
      } catch (err) {
        console.error("Error fetching /locations:", err);
      }
    };

    fetchAllLocations();
  }, []);

  // 获取用户的收藏列表
  useEffect(() => {
    const fetchFavorites = async () => {
      if (!user) {
        setLoading(false);
        return;
      }

      try {
        const token = localStorage.getItem("token");
        const res = await fetch("http://localhost:3000/favorite/my-all", {
          headers: {
            "Content-Type": "application/json",
            "Authorization": `Bearer ${token}`
          },
        });

        if (!res.ok) {
          if (res.status === 401) {
            console.warn("User not authenticated");
            setFavoriteLocations([]);
            setLoading(false);
            return;
          }
          throw new Error(`Server error: ${res.status}`);
        }

        const data = await res.json();
        
        // 提取收藏的地点信息
        const favorites = data
          .filter(item => item.location) // 只保留有地点信息的
          .map(item => ({
            favoriteId: item.favoriteId,
            locationId: item.locationId,
            name: item.location.name,
            events: Math.floor(Math.random() * 10) + 1, // 实际应用中应该从后端获取
            area: item.location.area || "Unknown",
          }));

        setFavoriteLocations(favorites);
        setLoading(false);
      } catch (err) {
        console.error("Error fetching favorites:", err);
        setFavoriteLocations([]);
        setLoading(false);
      }
    };

    fetchFavorites();
  }, [user]);

  // 取消收藏函数
  const handleRemoveFavorite = async (locationId, favoriteId) => {
    try {
      const token = localStorage.getItem("token");
      const res = await fetch(`http://localhost:3000/favorite/${locationId}`, {
        method: "DELETE",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${token}`
        },
      });

      if (!res.ok) {
        if (res.status === 404) {
          console.warn("Favorite not found, removing from local state");
        } else {
          throw new Error(`Failed to remove favorite: ${res.status}`);
        }
      }

      // 从状态中移除
      setFavoriteLocations(prev => 
        prev.filter(fav => 
          fav.favoriteId !== favoriteId && fav.locationId !== locationId
        )
      );

      console.log("Favorite removed successfully");
    } catch (err) {
      console.error("Error removing favorite:", err);
      alert("Failed to remove favorite. Please try again.");
    }
  };

  // 根据搜索词过滤收藏的地点 - 这个必须在所有渲染中无条件调用
  const filteredFavorites = useMemo(() => {
    if (!search.trim()) {
      return favoriteLocations;
    }

    const searchTerm = search.toLowerCase().trim();
    return favoriteLocations.filter(loc =>
      loc.name?.toLowerCase().includes(searchTerm) ||
      loc.area?.toLowerCase().includes(searchTerm)
    );
  }, [favoriteLocations, search]);

  // 如果没有用户登录，显示提示信息
  if (!user) {
    return (
      <main className="locations-page">
        <h2>⭐ Your Favourite Locations</h2>
        <div className="login-prompt">
          <p>Please log in to view your favorite locations.</p>
        </div>
      </main>
    );
  }

  if (loading) {
    return (
      <main className="locations-page">
        <h2>⭐ Your Favourite Locations</h2>
        <p>Loading your favorites...</p>
      </main>
    );
  }

  return (
    <main className="locations-page">
      <h2>⭐ Your Favourite Locations</h2>

      {/* 搜索框 */}
      <div className="filters">
        <div className="filter-item">
          <label htmlFor="search">Search in favorites:</label>
          <input
            id="search"
            type="text"
            placeholder="Search location name or area..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
          />
        </div>
      </div>

      {/* 收藏列表 */}
      <div className="table-container">
        {filteredFavorites.length > 0 ? (
          <table className="locations-table">
            <thead>
              <tr>
                <th>Location Name</th>
                <th>Number of Events</th>
                <th>Area</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {filteredFavorites.map((location) => (
                <tr key={location.favoriteId || location.locationId}>
                  <td>{location.name}</td>
                  <td>{location.events}</td>
                  <td>{location.area}</td>
                  <td>
                    <button
                      className="remove-btn"
                      onClick={() => handleRemoveFavorite(location.locationId, location.favoriteId)}
                      title="Remove from favorites"
                    >
                      Remove ❌
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : search.trim() ? (
          <div className="no-results">
            <p>No favorites found matching "{search}".</p>
            <button 
              className="clear-search-btn"
              onClick={() => setSearch("")}
            >
              Clear Search
            </button>
          </div>
        ) : (
          <div className="no-favorites">
            <p>You haven't added any locations to favorites yet.</p>
            <a href="/locations" className="browse-link">
              Browse Locations →
            </a>
          </div>
        )}
      </div>

      {/* 收藏统计 */}
      {filteredFavorites.length > 0 && (
        <div className="favorites-stats">
          <p>Showing {filteredFavorites.length} of {favoriteLocations.length} favorite locations</p>
        </div>
      )}
    </main>
  );
};

export default FavouritesPage;
