import React, { useState, useEffect, useMemo } from "react";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";
import "../styles/locations.css";

// 添加模态框组件
const EventModal = ({ isOpen, onClose, onSubmit, event = null, locations = [] }) => {
  const [formData, setFormData] = useState({
    name: "",
    description: "",
    presenter: "",
    time: "",
    locId: ""
  });

  useEffect(() => {
    if (event) {
      setFormData({
        name: event.name || "",
        description: event.description || "",
        presenter: event.presenter || "",
        time: event.time || "",
        locId: event.locId || ""
      });
    } else {
      setFormData({
        name: "",
        description: "",
        presenter: "",
        time: "",
        locId: ""
      });
    }
  }, [event]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(formData);
    onClose();
  };

  if (!isOpen) return null;

  return (
    <div style={{
      position: "fixed",
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      backgroundColor: "rgba(0,0,0,0.5)",
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      zIndex: 1000
    }}>
      <div style={{
        backgroundColor: "white",
        padding: "2rem",
        borderRadius: "8px",
        width: "90%",
        maxWidth: "500px",
        maxHeight: "90vh",
        overflowY: "auto"
      }}>
        <h3 style={{ marginBottom: "1.5rem", color: "#333" }}>
          {event ? "Edit Event" : "Add New Event"}
        </h3>
        
        <form onSubmit={handleSubmit}>
          <div style={{ marginBottom: "1rem" }}>
            <label style={{ display: "block", marginBottom: "0.3rem", fontWeight: "600" }}>
              Event Title *
            </label>
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              required
              style={{
                width: "100%",
                padding: "0.5rem",
                border: "1px solid #ccc",
                borderRadius: "4px"
              }}
            />
          </div>

          <div style={{ marginBottom: "1rem" }}>
            <label style={{ display: "block", marginBottom: "0.3rem", fontWeight: "600" }}>
              Description
            </label>
            <textarea
              name="description"
              value={formData.description}
              onChange={handleChange}
              rows="3"
              style={{
                width: "100%",
                padding: "0.5rem",
                border: "1px solid #ccc",
                borderRadius: "4px",
                resize: "vertical"
              }}
            />
          </div>

          <div style={{ marginBottom: "1rem" }}>
            <label style={{ display: "block", marginBottom: "0.3rem", fontWeight: "600" }}>
              Presenter
            </label>
            <input
              type="text"
              name="presenter"
              value={formData.presenter}
              onChange={handleChange}
              style={{
                width: "100%",
                padding: "0.5rem",
                border: "1px solid #ccc",
                borderRadius: "4px"
              }}
            />
          </div>

          <div style={{ marginBottom: "1rem" }}>
            <label style={{ display: "block", marginBottom: "0.3rem", fontWeight: "600" }}>
              Time (YYYY-MM-DD HH:mm) *
            </label>
            <input
              type="text"
              name="time"
              value={formData.time}
              onChange={handleChange}
              required
              placeholder="2024-12-25 14:30"
              style={{
                width: "100%",
                padding: "0.5rem",
                border: "1px solid #ccc",
                borderRadius: "4px"
              }}
            />
          </div>

          <div style={{ marginBottom: "1.5rem" }}>
            <label style={{ display: "block", marginBottom: "0.3rem", fontWeight: "600" }}>
              Venue (Location) *
            </label>
            <select
              name="locId"
              value={formData.locId}
              onChange={handleChange}
              required
              style={{
                width: "100%",
                padding: "0.5rem",
                border: "1px solid #ccc",
                borderRadius: "4px"
              }}
            >
              <option value="">Select a venue</option>
              {locations.map(loc => (
                <option key={loc.id} value={loc.id}>
                  {loc.name}
                </option>
              ))}
            </select>
          </div>

          <div style={{ display: "flex", justifyContent: "flex-end", gap: "1rem" }}>
            <button
              type="button"
              onClick={onClose}
              style={{
                padding: "0.6rem 1.2rem",
                backgroundColor: "#6c757d",
                color: "white",
                border: "none",
                borderRadius: "4px",
                cursor: "pointer"
              }}
            >
              Cancel
            </button>
            <button
              type="submit"
              style={{
                padding: "0.6rem 1.2rem",
                backgroundColor: "#2c7be5",
                color: "white",
                border: "none",
                borderRadius: "4px",
                cursor: "pointer"
              }}
            >
              {event ? "Update" : "Add"} Event
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

// 描述文本组件，支持折叠/展开
const DescriptionText = ({ text, maxLength = 100 }) => {
  const [isExpanded, setIsExpanded] = useState(false);
  
  if (!text) return <span style={{ color: "#999" }}>No description</span>;
  
  const shouldTruncate = text.length > maxLength && !isExpanded;
  const displayText = shouldTruncate ? text.substring(0, maxLength) + "..." : text;
  
  return (
    <div>
      <span>{displayText}</span>
      {text.length > maxLength && (
        <button
          onClick={() => setIsExpanded(!isExpanded)}
          style={{
            marginLeft: "0.5rem",
            color: "#2c7be5",
            background: "none",
            border: "none",
            cursor: "pointer",
            fontSize: "0.85rem",
            textDecoration: "underline"
          }}
        >
          {isExpanded ? "less" : "more"}
        </button>
      )}
    </div>
  );
};

const AdminEventsPage = () => {
  const [events, setEvents] = useState([]);
  const [locations, setLocations] = useState([]);
  const [search, setSearch] = useState("");
  const [loading, setLoading] = useState(true);
  const [modalOpen, setModalOpen] = useState(false);
  const [editingEvent, setEditingEvent] = useState(null);

  // === Load events and locations on mount ===
  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch events
        const resEvents = await fetch("/admin/events", {
          headers: { "Content-Type": "application/json" },
          credentials: "include",
        });

        if (!resEvents.ok) throw new Error("Failed to fetch events");
        const dataEvents = await resEvents.json();

        // Fetch locations for venue lookup
        const resLoc = await fetch("/locations", {
          headers: { "Content-Type": "application/json" },
        });
        const dataLoc = resLoc.ok ? await resLoc.json() : [];

        setEvents(dataEvents);
        setLocations(dataLoc);
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  // === Search filter ===
  const filteredEvents = useMemo(() => {
    return events.filter((ev) =>
      ev.name?.toLowerCase().includes(search.toLowerCase())
    );
  }, [events, search]);

  // === Helper to get venue name by locid ===
  const getVenueName = (locId) => {
    const loc = locations.find((l) => l.id === locId);
    return loc ? loc.name : "Unknown";
  };

  // === Modal handlers ===
  const handleAddClick = () => {
    setEditingEvent(null);
    setModalOpen(true);
  };

  const handleEditClick = (event) => {
    setEditingEvent(event);
    setModalOpen(true);
  };

  const handleModalSubmit = async (formData) => {
    try {
      if (editingEvent) {
        // Update existing event
        const res = await fetch(`/admin/events/${editingEvent.id}`, {
          method: "PUT",
          headers: { "Content-Type": "application/json" },
          credentials: "include",
          body: JSON.stringify(formData),
        });
        if (!res.ok) throw new Error("Failed to update event");
        const updated = await res.json();
        setEvents((prev) => prev.map((e) => (e.id === editingEvent.id ? updated : e)));
      } else {
        // Add new event
        const res = await fetch("/admin/events", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          credentials: "include",
          body: JSON.stringify(formData),
        });
        if (!res.ok) throw new Error("Failed to add event");
        const newEvent = await res.json();
        setEvents((prev) => [...prev, newEvent]);
      }
    } catch (err) {
      console.error(err);
      alert(`Error ${editingEvent ? "updating" : "adding"} event`);
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this event?")) return;
    try {
      const res = await fetch(`/admin/events/${id}`, {
        method: "DELETE",
        credentials: "include",
      });
      if (!res.ok) throw new Error("Failed to delete event");
      setEvents((prev) => prev.filter((e) => e.id !== id));
    } catch (err) {
      console.error(err);
      alert("Error deleting event");
    }
  };

  if (loading) {
    return (
      <main className="locations-page">
        <h2>Admin Events</h2>
        <p>Loading events from server...</p>
      </main>
    );
  }

  return (
    <main className="locations-page">
      <h2>Admin Events</h2>

      {/* === Search & Add Section === */}
      <div className="filters" style={{ justifyContent: "space-between", alignItems: "flex-end" }}>
        <div className="filter-item" style={{ flex: 1 }}>
          <label htmlFor="search">Search event title:</label>
          <input
            id="search"
            type="text"
            placeholder="Enter event title..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
          />
        </div>

        <button
          onClick={handleAddClick}
          style={{
            padding: "0.6rem 1rem",
            backgroundColor: "#2c7be5",
            color: "white",
            border: "none",
            borderRadius: "6px",
            cursor: "pointer",
            height: "fit-content",
            minHeight: "38px"
          }}
        >
          + Add New Event
        </button>
      </div>

      {/* === Table === */}
      <div className="table-container">
        <table className="locations-table" style={{ minWidth: "1000px" }}>
          <thead>
            <tr>
              <th style={{ width: "5%" }}>ID</th>
              <th style={{ width: "15%" }}>Title</th>
              <th style={{ width: "25%" }}>Description</th>
              <th style={{ width: "15%" }}>Venue</th>
              <th style={{ width: "15%" }}>Presenter</th>
              <th style={{ width: "15%" }}>Time</th>
              <th style={{ width: "10%" }}>Actions</th>
            </tr>
          </thead>
          <tbody>
            {filteredEvents.length > 0 ? (
              filteredEvents.map((ev) => (
                <tr key={ev.id}>
                  <td>{ev.id}</td>
                  <td>{ev.name}</td>
                  <td>
                    <DescriptionText text={ev.description} maxLength={80} />
                  </td>
                  <td>{getVenueName(ev.locId)}</td>
                  <td>{ev.presenter}</td>
                  <td>{ev.time}</td>
                  <td>
                    <div style={{ display: "flex", gap: "0.5rem", flexWrap: "wrap" }}>
                      <button
                        style={{
                          backgroundColor: "#ffc107",
                          border: "none",
                          padding: "0.4rem 0.7rem",
                          borderRadius: "4px",
                          cursor: "pointer",
                          whiteSpace: "nowrap"
                        }}
                        onClick={() => handleEditClick(ev)}
                      >
                        Update
                      </button>
                      <button
                        style={{
                          backgroundColor: "#dc3545",
                          border: "none",
                          padding: "0.4rem 0.7rem",
                          borderRadius: "4px",
                          color: "white",
                          cursor: "pointer",
                          whiteSpace: "nowrap"
                        }}
                        onClick={() => handleDelete(ev.id)}
                      >
                        Delete
                      </button>
                    </div>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="7" className="no-results">
                  No events found
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>

      {/* Event Modal */}
      <EventModal
        isOpen={modalOpen}
        onClose={() => setModalOpen(false)}
        onSubmit={handleModalSubmit}
        event={editingEvent}
        locations={locations}
      />
    </main>
  );
};

export default AdminEventsPage;
